insert into 0_mmf_wp_posts (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_category, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) values ('43', '2', '2008-03-15 20:43:27', '2008-03-15 19:43:27', '<strong>C\'est une variante des pains au lait <ins>(<a href=\"http://www.vemasson.com/_mamanfanny/index.php?entry=entry061205-221605\" target=\"_blank\">recette</a>)</ins>

